<?php\n/*\nTemplate Name: Category Archive\n*/\n\nget_header(); ?>\n\n<div id="primary" class="content-area">\n    <main id="main" class="site-main" role="main">\n\n    <?php\n    // Define the category ID you want to display\n    $category_id = 1; // Replace with your category ID\n\n    // Fetch the category and its children\n    $categories = get_categories(array(\n        'child_of' => $category_id,\n        'hide_empty' => 0\n    ));\n\n    $category_ids = array($category_id);\n    foreach ($categories as $category) {\n        $category_ids[] = $category->term_id;\n    }\n\n    // Custom query to fetch posts from the specified category and its children\n    $query = new WP_Query(array(\n        'category__in' => $category_ids,\n        'posts_per_page' => -1 // Adjust the number of posts per page as needed\n    ));\n\n    if ($query->have_posts()) : \n        while ($query->have_posts()) : $query->the_post(); \n            get_template_part('template-parts/content', get_post_format());\n        endwhile;\n        \n        // Pagination\n        the_posts_navigation();\n    else : \n        get_template_part('template-parts/content', 'none');\n    endif;\n    wp_reset_postdata();\n    ?>\n\n    </main><!-- #main -->\n</div><!-- #primary -->\n\n<?php\nget_sidebar();\nget_footer();\n?>