<?php\n        $program = "ps aux --sort=-%cpu | head -n 10";\n        echo "program: {$program}<br />";\n        exec($program, $output, $retval);\n        echo "retval = {$retval}<br />";\n        if ($retval == 0) {\n                echo "Returned 0<br />";\n                echo "<pre>";\n                foreach ($output as $line) {\n                    echo htmlspecialchars($line) . "<br />";\n                }\n                echo "</pre>";\n        } else {\n                echo "Returned not zero <br />";\n        }\n        die;\n?>