$path = '/path/to/your/file/';\n$file_name = 'example.txt';\n\n// Full path to the file\n$full_path = $path . $file_name;\n\n// Check if the file exists using file_exists()\nif (file_exists($full_path)) {\n    echo "The file or directory exists.";\n} else {\n    echo "The file or directory does not exist.";\n}\n\n// Check if the file exists and is a regular file using is_file()\nif (is_file($full_path)) {\n    echo "The path exists and is a regular file.";\n} else {\n    echo "The path does not exist or it is not a regular file.";\n}