function cleanInputs() {\n    $cGet = [];\n    $cPost = [];\n\n    $sanitize = function($key, $value) {\n        $prefix = substr($key, 0, 2);\n        switch ($prefix) {\n            case 'in':\n                // Integer input\n                return filter_var($value, FILTER_VALIDATE_INT);\n            case 'tx':\n                // Text input (letters, numbers, some symbols)\n                return preg_match('/^[\w\s\-.,!]*$/', $value) ? $value : false;\n            case 'em':\n                // Email input\n                return filter_var($value, FILTER_VALIDATE_EMAIL);\n            default:\n                return false;\n        }\n    };\n\n    foreach ($_GET as $taintedKey => $taintedValue) {\n        $cGet[$taintedKey] = $sanitize($taintedKey, $taintedValue);\n    }\n\n    foreach ($_POST as $taintedKey => $taintedValue) {\n        $cPost[$taintedKey] = $sanitize($taintedKey, $taintedValue);\n    }\n\n    return ['cGet' => $cGet, 'cPost' => $cPost];\n}\n\n// Usage example:\n$cleanData = cleanInputs();\n$cGet = $cleanData['cGet'];\n$cPost = $cleanData['cPost'];