<?php\n$logfile = '/path/to/your/logfile.log'; // Make sure you correctly set the path\n$escapedLogFile = escapeshellarg($logfile); // Escape the file path to prevent shell injection\n\n$line = shell_exec("tail -n 1 $escapedLogFile"); // Execute the command and get the last line\nif ($line === null) {\n    // Handle the error if the command execution fails\n    echo "Failed to read the log file.";\n} else {\n    echo $line; // Output the last line\n}\n?>