<?php\n// Database connection details for db1\n$db1_host = 'db1_host';\n$db1_user = 'db1_user';\n$db1_pass = 'db1_pass';\n$db1_name = 'db1';\n\n// Database connection details for db2\n$db2_host = 'db2_host';\n$db2_user = 'db2_user';\n$db2_pass = 'db2_pass';\n$db2_name = 'db2';\n\n// Connect to db1\n$conn1 = new mysqli($db1_host, $db1_user, $db1_pass, $db1_name);\nif ($conn1->connect_error) {\n    die("Connection failed: " . $conn1->connect_error);\n}\n\n// Connect to db2\n$conn2 = new mysqli($db2_host, $db2_user, $db2_pass, $db2_name);\nif ($conn2->connect_error) {\n    die("Connection failed: " . $conn2->connect_error);\n}\n\n// Fetch IDs from db2\n$sql = "SELECT id FROM accounts";\n$result = $conn2->query($sql);\n\nif ($result->num_rows > 0) {\n    $ids = [];\n    while ($row = $result->fetch_assoc()) {\n        $ids[] = $row['id'];\n    }\n    $id_list = implode(',', $ids);\n\n    // Delete rows from db1\n    $delete_sql = "DELETE FROM players WHERE acc NOT IN ($id_list)";\n    if ($conn1->query($delete_sql) === TRUE) {\n        echo "Records deleted successfully";\n    } else {\n        echo "Error deleting records: " . $conn1->error;\n    }\n} else {\n    echo "No records found in db2.accounts";\n}\n\n// Close connections\n$conn1->close();\n$conn2->close();\n?>