<?php\nfunction cropCircle($sourcePath, $destinationPath) {\n    $src = imagecreatefromjpeg($sourcePath);\n    $width = imagesx($src);\n    $height = imagesy($src);\n    $size = min($width, $height);\n\n    $mask = imagecreatetruecolor($size, $size);\n    $bg = imagecolorallocate($mask, 0, 0, 0);\n    $fg = imagecolorallocate($mask, 255, 255, 255);\n    imagecolortransparent($mask, $bg);\n    imagefilledellipse($mask, $size / 2, $size / 2, $size, $size, $fg);\n\n    $dest = imagecreatetruecolor($size, $size);\n    imagecopyresampled($dest, $src, 0, 0, 0, 0, $size, $size, $width, $height);\n    imagecopymerge($dest, $mask, 0, 0, 0, 0, $size, $size, 100);\n\n    imagecolortransparent($dest, $bg);\n    imagepng($dest, $destinationPath);\n\n    imagedestroy($src);\n    imagedestroy($mask);\n    imagedestroy($dest);\n}\n\n// Usage\ncropCircle('input.jpg', 'output.png');\n?>