// Define a custom route with a callback\nRoute::set('hyphenated', '<controller>(/<action>(/<id>))', array(\n    'controller' => '[a-z\-]+',\n    'action'     => '[a-z\-]+',\n    'id'         => '[0-9]+',\n))\n->filter(function($route, $params, $request) {\n    // Convert the controller name from hyphenated to camel case\n    $controller = str_replace(' ', '', ucwords(str_replace('-', ' ', $params['controller'])));\n    $params['controller'] = $controller;\n\n    // Convert the action name from hyphenated to camel case, if it exists\n    if (isset($params['action'])) {\n        $action = str_replace(' ', '', ucwords(str_replace('-', ' ', $params['action'])));\n        $params['action'] = lcfirst($action);\n    }\n\n    return $params;\n});