class view {\n    public static function getUserSelector($options = []) {\n        $onSelect = isset($options['onSelect']) ? $options['onSelect'] : 'function() {}';\n        echo '<div id="user_selector" data-on-select="' . htmlspecialchars($onSelect, ENT_QUOTES, 'UTF-8') . '">';\n        echo '    <div class="user">';\n        echo '        <h1>Username1</h1>';\n        echo '        <a href="javascript:void(0);" onclick="selectUser(this);">select</a>';\n        echo '    </div>';\n        echo '    <div class="user">';\n        echo '        <h1>Username2</h1>';\n        echo '        <a href="javascript:void(0);" onclick="selectUser(this);">select</a>';\n        echo '    </div>';\n        echo '</div>';\n    }\n}\n\n// Usage\nview::getUserSelector(['onSelect' => 'function() { alert(this.id); }']);