<?php\n// Define the path to the Swish-e executable and the index file\n$swishPath = '/usr/local/bin/swish-e';\n$indexFile = '/path/to/index.swish-e';\n\n// Get the search query from user input (e.g., a form submission)\n$query = isset($_GET['query']) ? $_GET['query'] : '';\n\n// Escape the query parameter to prevent command injection\n$escapedQuery = escapeshellarg($query);\n\n// Build the command to execute Swish-e search\n$command = "$swishPath -w $escapedQuery -f $indexFile -H 2";\n\n// Execute the command and capture the output\n$output = shell_exec($command);\n\n// Display the output\necho "<pre>$output</pre>";\n?>