<?php\n// Email server credentials\n$hostname = '{imap.example.com:993/imap/ssl}INBOX'; // Replace with your IMAP server details\n$username = 'your-email@example.com'; // Replace with your email\n$password = 'your-email-password'; // Replace with your email password\n\n// Connect to the mail server\n$inbox = imap_open($hostname, $username, $password) or die('Cannot connect to Mail: ' . imap_last_error());\n\n// Fetch emails\n$emails = imap_search($inbox, 'ALL');\n\n// Check if emails are found\nif ($emails) {\n    // Sort emails by date in descending order\n    rsort($emails);\n\n    // Loop through each email\n    foreach ($emails as $email_number) {\n        // Fetch the email overview\n        $overview = imap_fetch_overview($inbox, $email_number, 0);\n\n        // Fetch the email body\n        $message = imap_fetchbody($inbox, $email_number, 1);\n\n        // Process the email\n        echo "Subject: " . $overview[0]->subject . "\n";\n        echo "From: " . $overview[0]->from . "\n";\n        echo "Date: " . $overview[0]->date . "\n";\n        echo "Message: " . $message . "\n\n";\n\n        // Add your processing logic here\n    }\n}\n\n// Close the connection\nimap_close($inbox);\n?>