public function beforeValidate($options = [])\n{\n    parent::beforeValidate($options);\n\n    // Get a list of all controllers\n    $controllers = App::objects('controller');\n\n    // Remove the "Controller" suffix from the controller names\n    foreach ($controllers as $key => $controller) {\n        $controllers[$key] = str_replace('Controller', '', $controller);\n    }\n\n    // Check if the username matches any controller names\n    if (in_array($this->data['User']['username'], $controllers)) {\n        $this->invalidate('username', 'This username cannot be used.');\n        return false;\n    }\n\n    return true;\n}