$response = $client->call('branchzipdata', $param);\n\nif ($response === null) {\n    die("Error: No response received from SOAP call.");\n}\n\n// Decode the base64 encoded response\n$content = base64_decode($response);\nif ($content === false) {\n    die("Error: Failed to decode base64 response.");\n}\n\n$filename = "output_zip.gz";\n\n// Open the file for writing (use 'wb' for binary-safe writing)\n$handle = fopen($filename, 'wb');\nif ($handle === false) {\n    die("Error: Cannot open file ($filename) for writing.");\n}\n\n// Write the content to the file\nif (fwrite($handle, $content) === false) {\n    fclose($handle);\n    die("Error: Cannot write to file ($filename).");\n}\n\n// Close the file handle\nfclose($handle);\n\n// Decompress the gzip file\n$decompressedFilename = "output_zip";\n$decompressCmd = "gzip -d -c $filename > $decompressedFilename";\n\nexec($decompressCmd, $output, $returnVar);\nif ($returnVar !== 0) {\n    die("Error: Failed to decompress the gzip file.");\n}\n\necho "File saved and decompressed successfully.";