public function select($psTableName, $paFields = "", $paWhere = array())\n{\n    //initial return value\n    $lbReturn = false;\n    try\n    {\n        $lsQuery = "SELECT * FROM `";\n        $lsQuery .= $psTableName;\n        $lsQuery .= "` ";\n        if (!empty($paWhere)) {\n            $lsQuery .= "WHERE ";\n            $conditions = array();\n            foreach ($paWhere as $lsKey => $lsValue) {\n                $conditions[] = $lsKey . " = '" . mysql_real_escape_string($lsValue) . "'";\n            }\n            $lsQuery .= implode(" AND ", $conditions);\n        }\n\n        //echo $lsQuery;\n        //execute $lsQuery\n        $this->msLastQuery = $lsQuery;\n        if (!$this->execute()) {\n            throw new DBException("Select failed, unable to execute query: " . $lsQuery);\n        } else {\n            $lbReturn = true;\n        }\n    }\n    catch (DBException $errorMsg)\n    {\n        ErrorHandler::handleException($errorMsg);\n    }\n    return $lbReturn;\n}