function truncateString($string, $wordLimit = 20) {\n    $words = explode(' ', $string);\n    if (count($words) > $wordLimit) {\n        $words = array_slice($words, 0, $wordLimit);\n        return implode(' ', $words) . '...';\n    }\n    return $string;\n}\n\n// Example usage:\n$inputString = "Your input string goes here and it may be longer than twenty words.";\n$truncatedString = truncateString($inputString);\necho $truncatedString;