<?php\n$xmlFile = 'path/to/your/largefile.xml';\n\n// Database connection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$reader = new XMLReader();\nif (!$reader->open($xmlFile)) {\n    die("Failed to open XML file.");\n}\n\n// Prepare an SQL statement for inserting data\n$stmt = $mysqli->prepare("INSERT INTO your_table (column1, column2) VALUES (?, ?)");\n$stmt->bind_param('ss', $value1, $value2);\n\nwhile ($reader->read()) {\n    if ($reader->nodeType == XMLReader::ELEMENT && $reader->localName == 'yourElement') {\n        $value1 = $reader->getAttribute('attribute1');\n        $value2 = $reader->getAttribute('attribute2');\n\n        // Execute the prepared statement\n        $stmt->execute();\n    }\n}\n\n// Close the statement and the database connection\n$stmt->close();\n$mysqli->close();\n\n// Close the XML file\n$reader->close();\n?>