// Define the callback function to base64 encode the matched parameter\nfunction encrypt_param($matches) {\n    // Base64 encode the matched document part\n    return 'href="somepage.php?x=' . base64_encode($matches[1]) . '"';\n}\n\n// Example web document\n$webdoc = '<a href="somepage.php?x=some_document.htm">click here</a>';\n\n// Use preg_replace_callback to find and replace the URLs\n$webdoc = preg_replace_callback(\n    '/href="somepage.php\?x=([^"]+)"/', // Regex pattern to match the URLs\n    'encrypt_param',                    // Callback function to process each match\n    $webdoc                            // Input web document\n);\n\n// Output the modified web document\necho $webdoc;