session_start();\nsession_write_close(); // Close the session to prevent session locking\n\n$fileName = $_POST['textFile'];\n$serverURL = $_SESSION['serverURL'] . "fileReader.php?textFile=$fileName";\n\n$multiHandle = curl_multi_init();\n$curlHandle = curl_init($serverURL);\n\ncurl_setopt($curlHandle, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($curlHandle, CURLOPT_TIMEOUT, 10); // Set a timeout\n\ncurl_multi_add_handle($multiHandle, $curlHandle);\n\n$active = null;\ndo {\n    $mrc = curl_multi_exec($multiHandle, $active);\n} while ($mrc == CURLM_CALL_MULTI_PERFORM);\n\nwhile ($active && $mrc == CURLM_OK) {\n    if (curl_multi_select($multiHandle) != -1) {\n        do {\n            $mrc = curl_multi_exec($multiHandle, $active);\n        } while ($mrc == CURLM_CALL_MULTI_PERFORM);\n    }\n}\n\n$result = curl_multi_getcontent($curlHandle);\n\ncurl_multi_remove_handle($multiHandle, $curlHandle);\ncurl_multi_close($multiHandle);\n\necho $result;