// Read the file content and convert encoding from UTF-16 to UTF-8\n$fileContent = file_get_contents($tmpfile);\n$fileContent = mb_convert_encoding($fileContent, 'UTF-8', 'UTF-16');\n\n// Replace tab characters with semicolons\n$fileContent = str_replace("\t", ";", $fileContent);\n\n// Write the converted content back to a temporary file\nfile_put_contents($tmpfile, $fileContent);\n\n// Open the temporary file for reading\n$filehandle = fopen($tmpfile, 'r');\n\n// Initialize an array to store the values\n$values = [];\n\n// Read and process the CSV data\nwhile (($data = fgetcsv($filehandle, 1000, ";")) !== FALSE) {\n    $values[] = array(\n        'id' => $data[0],\n        'type' => $data[1],\n        'text' => $data[4],\n        'desc' => $data[5],\n        'pdf' => $data[7]\n    );\n}\n\n// Close the file handle\nfclose($filehandle);\n\n// Example of inserting data into the database\n// Assuming you have a PDO connection $pdo\nforeach ($values as $row) {\n    $stmt = $pdo->prepare("INSERT INTO your_table_name (id, type, text, desc, pdf) VALUES (?, ?, ?, ?, ?)");\n    $stmt->execute([$row['id'], $row['type'], $row['text'], $row['desc'], $row['pdf']]);\n}