require 'vendor/autoload.php';\n\nuse Google\Client;\nuse Google\Service\Calendar;\n\n// Set up the client\n$client = new Client();\n$client->setApplicationName('Your Application Name');\n$client->setScopes(Calendar::CALENDAR_READONLY);\n$client->setAuthConfig('path/to/your/credentials.json');\n$client->setAccessType('offline');\n\n// Initialize the Calendar service\n$service = new Calendar($client);\n\n// Define the ID of the calendar to fetch events from\n$calendarId = 'your_calendar_id@group.calendar.google.com';\n\n// Set the parameters for the request\n$params = [\n    'maxResults' => 5,\n    'orderBy' => 'startTime',\n    'singleEvents' => true,\n    'timeMin' => date('c'), // current time in RFC3339 format\n];\n\n// Make the request\n$results = $service->events->listEvents($calendarId, $params);\n\nif (count($results->getItems()) == 0) {\n    echo "No upcoming events found.";\n} else {\n    foreach ($results->getItems() as $event) {\n        $start = $event->start->dateTime;\n        if (empty($start)) {\n            $start = $event->start->date;\n        }\n        printf("%s (%s)\n", $event->getSummary(), $start);\n        echo "Location: " . $event->getLocation() . "\n";\n        echo "Link: " . $event->htmlLink . "\n";\n    }\n}