<?php\n// Example URL: http://example.com/download.php?id=1234\n\n// Function to determine which server to use\nfunction getServerUrl($fileId) {\n    // Implement your logic here to choose the server based on the file ID or other conditions\n    if (someCondition()) {\n        return "http://servera.com/{$fileId}.exe";\n    } else {\n        return "http://serverb.com/{$fileId}.exe";\n    }\n}\n\n// Get the file ID from the request\n$fileId = isset($_GET['id']) ? $_GET['id'] : null;\n\nif ($fileId) {\n    $downloadUrl = getServerUrl($fileId);\n    // Redirect the user to the download URL\n    header("Location: " . $downloadUrl);\n    exit();\n} else {\n    // Handle the error - file ID not provided\n    header("HTTP/1.1 400 Bad Request");\n    echo "Invalid Request";\n}\n?>