function get_content($a, $b, $c = "00")\n{\n    $request = "arg01=" . $a;\n    $request .= "&arg02=" . $b;\n    $request .= "&arg03=" . $c;\n\n    $host = "www.site.com";\n    $script = "/page.php";\n    $method = "POST";\n\n    $request_length = strlen($request);\n\n    $header = "$method $script HTTP/1.1\r\n";\n    $header .= "Host: $host\r\n";\n    $header .= "Content-Type: application/x-www-form-urlencoded\r\n";\n    $header .= "Content-Length: $request_length\r\n";\n    $header .= "Connection: close\r\n\r\n";\n    $header .= "$request\r\n";\n\n    $socket = @fsockopen($host, 80, $errno, $errstr);\n    if ($socket) {\n        fputs($socket, $header);\n        $output = '';\n        while (!feof($socket)) {\n            $output .= fgets($socket);\n        }\n        fclose($socket);\n    }\n\n    // Split headers and body\n    list($headers, $body) = explode("\r\n\r\n", $output, 2);\n\n    // Check if Transfer-Encoding is chunked\n    if (strpos($headers, "Transfer-Encoding: chunked") !== false) {\n        $body = decode_chunked($body);\n    }\n\n    return $body;\n}\n\nfunction decode_chunked($str)\n{\n    $pos = 0;\n    $len = strlen($str);\n    $dechunked = null;\n\n    while (($pos < $len) && ($chunk_size_hex = strstr(substr($str, $pos), "\r\n", true))) {\n        $chunk_size = hexdec($chunk_size_hex);\n        $pos += strlen($chunk_size_hex) + 2;\n\n        if ($chunk_size === 0) {\n            break;\n        }\n\n        $dechunked .= substr($str, $pos, $chunk_size);\n        $pos += $chunk_size + 2;\n    }\n\n    return $dechunked;\n}