<?php\nrequire_once 'crud.php';\n\n// Create example\n$data = [\n    'title' => 'Sample Title',\n    'content' => 'Sample Content',\n    'author' => 'John Doe'\n];\ncreate('articles', $data);\n\n// Read example\n$articles = read('articles');\nforeach ($articles as $article) {\n    echo $article['title'] . '<br>';\n}\n\n// Update example\n$updateData = [\n    'title' => 'Updated Title'\n];\nupdate('articles', $updateData, "id=1");\n\n// Delete example\ndelete('articles', "id=1");\n?>