// mymodule.module\n   function mymodule_menu() {\n     $items = array();\n     $items['mymodule/userinfo'] = array(\n       'title' => 'User Info',\n       'page callback' => 'mymodule_userinfo',\n       'access callback' => 'user_is_logged_in',\n       'type' => MENU_CALLBACK,\n     );\n     return $items;\n   }\n\n   function mymodule_userinfo() {\n     global $user;\n     // Return user data as JSON\n     drupal_json_output(array('uid' => $user->uid, 'name' => $user->name));\n   }