<?php\n$tag = 'php';\n$stmt = $pdo->prepare("SELECT Items.item_id, Items.item_name\n                       FROM Items\n                       JOIN ItemTags ON Items.item_id = ItemTags.item_id\n                       JOIN Tags ON ItemTags.tag_id = Tags.tag_id\n                       WHERE Tags.tag_name = :tag");\n$stmt->execute(['tag' => $tag]);\n$items = $stmt->fetchAll();\n\nforeach ($items as $item) {\n    echo $item['item_name'] . '<br>';\n}\n?>