<?php\n$item_id = 1;\n$stmt = $pdo->prepare("SELECT Tags.tag_id, Tags.tag_name\n                       FROM Tags\n                       JOIN ItemTags ON Tags.tag_id = ItemTags.tag_id\n                       JOIN Items ON ItemTags.item_id = Items.item_id\n                       WHERE Items.item_id = :item_id");\n$stmt->execute(['item_id' => $item_id]);\n$tags = $stmt->fetchAll();\n\nforeach ($tags as $tag) {\n    echo $tag['tag_name'] . '<br>';\n}\n?>