<?php\nfunction isSearchEngineBot() {\n    $userAgents = [\n        'Googlebot',       // Google\n        'Bingbot',         // Bing\n        'Slurp',           // Yahoo\n        'DuckDuckBot',     // DuckDuckGo\n        'Baiduspider',     // Baidu\n        'YandexBot',       // Yandex\n        'Sogou',           // Sogou\n        'Exabot',          // Exalead\n        'facebot',         // Facebook\n        'ia_archiver'      // Alexa\n    ];\n\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n\n    foreach ($userAgents as $bot) {\n        if (stripos($userAgent, $bot) !== false) {\n            return true;\n        }\n    }\n    return false;\n}\n\nif (isSearchEngineBot()) {\n    echo 'A search engine bot is visiting your site.';\n} else {\n    echo 'This is a regular visitor.';\n}\n?>