function getExternalData($type = 'GET') {\n    $type = strtoupper($type);\n    $data = $_$type;\n    $clean = [];\n    foreach ($data as $key => $value) {\n        // Perform necessary validation and sanitization for each expected input\n        $clean[$key] = filter_var($value, FILTER_SANITIZE_STRING);\n    }\n    return $clean;\n}\n\nfunction insertDataIntoDatabase($data) {\n    $dsn = 'mysql:host=your_host;dbname=your_dbname';\n    $username = 'your_username';\n    $password = 'your_password';\n\n    try {\n        $pdo = new PDO($dsn, $username, $password);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        $stmt = $pdo->prepare('INSERT INTO your_table (column1, column2) VALUES (:value1, :value2)');\n        $stmt->bindParam(':value1', $data['value1']);\n        $stmt->bindParam(':value2', $data['value2']);\n\n        $stmt->execute();\n    } catch (PDOException $e) {\n        echo 'Database error: ' . $e->getMessage();\n    }\n}\n\n// Example usage\n$data = getExternalData('POST');\ninsertDataIntoDatabase($data);