<?php\n$search_terms = explode(' ', $search_query);\n$search_query = "";\n$params = [];\n\nforeach ($search_terms as $term) {\n    if (!empty($search_query)) {\n        $search_query .= " AND ";\n    }\n    $search_query .= "(blog_title LIKE ? OR blog_content LIKE ?)";\n    $params[] = '%' . $term . '%';\n    $params[] = '%' . $term . '%';\n}\n\n$sql_blog = "SELECT * FROM blog WHERE $search_query ORDER BY blog_title";\n$stmt = $pdo->prepare($sql_blog);\n$stmt->execute($params);\n$results = $stmt->fetchAll();\n\n// Repeat for links and pages...\n?>