<?php\n$search_terms = explode(' ', $search_query);\n$search_query = "";\n\nforeach ($search_terms as $term) {\n    if (!empty($search_query)) {\n        $search_query .= " AND ";\n    }\n    $search_query .= "(blog_title LIKE '%$term%' OR blog_content LIKE '%$term%')";\n}\n\n$sql_blog = "SELECT * FROM blog WHERE $search_query ORDER BY blog_title";\n$sql_links = "SELECT * FROM links WHERE $search_query ORDER BY link_title";\n$sql_pages = "SELECT * FROM pages WHERE $search_query ORDER BY page_title";\n\n// Execute the queries and handle the results...\n?>