class MyObject {\n    const FLAG_A = 1;       // 0001\n    const FLAG_B = 2;       // 0010\n    const FLAG_C = 4;       // 0100\n    const FLAG_D = 8;       // 1000\n\n    private $flags = 0;\n\n    // Constructor to initialize flags if needed\n    public function __construct($flags = 0) {\n        $this->flags = $flags;\n    }\n\n    // Methods to set, unset and check flags\n    public function setFlag($flag) {\n        $this->flags |= $flag;\n    }\n\n    public function unsetFlag($flag) {\n        $this->flags &= ~$flag;\n    }\n\n    public function isFlagSet($flag) {\n        return ($this->flags & $flag) != 0;\n    }\n\n    // Method to get all flags as an integer\n    public function getFlags() {\n        return $this->flags;\n    }\n}