<?php\n// Define the base directory\n$baseDir = '/var/www/vhosts/myDomain.com/httpdocs/scripts/';\n\n// Define the directory and file name\n$fileDir = $baseDir . 'fileDirectory/';\n$fileName = '4.txt'; // Example file name\n$fileContents = 'Your file contents here';\n\n// Ensure the directory exists\nif (!is_dir($fileDir)) {\n    mkdir($fileDir, 0755, true);\n}\n\n// Write the file using the absolute path\n$filePath = $fileDir . $fileName;\nif (file_put_contents($filePath, $fileContents) === false) {\n    echo "Failed to write to file: $filePath\n";\n} else {\n    echo "File written successfully: $filePath\n";\n}\n?>