function hasMultipleOccurrences($string) {\n    // Convert the string to lowercase to make the check case-insensitive\n    $string = strtolower($string);\n\n    // Split the string into words\n    $words = str_word_count($string, 1);\n\n    // Count the occurrences of each word\n    $wordCounts = array_count_values($words);\n\n    // Check for any word with more than one occurrence\n    foreach ($wordCounts as $count) {\n        if ($count > 1) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\n// Test examples\n$testString1 = "google makes love";\n$testString2 = "google makes google love";\n\necho hasMultipleOccurrences($testString1) ? 'Not accepted' : 'Accepted';  // Output: Accepted\necho "\n";\necho hasMultipleOccurrences($testString2) ? 'Not accepted' : 'Accepted';  // Output: Not accepted