// CONNECT TO DB HERE\n\n$sub = new YomoSubscription(95, 783);\n\n// Warm-up the cache by running the query a few times\nfor ($i = 0; $i < 5; $i++) {\n    $sub->generateFingerprint();\n}\n\n// Measure the query execution time multiple times\n$iterations = 10;\n$totalTime = 0;\n\nfor ($i = 0; $i < $iterations; $i++) {\n    $t = microtime(true);\n\n    // contains the SQL db call i'm testing\n    $sub->generateFingerprint();\n\n    $totalTime += (microtime(true) - $t);\n}\n\n// Calculate the average execution time\n$averageTime = $totalTime / $iterations;\n\necho "Average execution time: " . $averageTime . " seconds\n";