<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_FILES['file'])) {\n    $file = $_FILES['file']['tmp_name'];\n    \n    // Ensure the file was successfully uploaded to Server A\n    if (is_uploaded_file($file)) {\n        // Proceed to upload the file to Server B using cURL\n        $ch = curl_init();\n        $cfile = new CURLFile($file, $_FILES['file']['type'], $_FILES['file']['name']);\n\n        $data = array('file' => $cfile);\n\n        curl_setopt($ch, CURLOPT_URL, 'http://server-b/upload.php');\n        curl_setopt($ch, CURLOPT_POST, true);\n        curl_setopt($ch, CURLOPT_POSTFIELDS, $data);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n        $response = curl_exec($ch);\n        curl_close($ch);\n\n        // Handle the response from Server B\n        if ($response === false) {\n            echo 'Error uploading to Server B';\n        } else {\n            echo 'File successfully uploaded to Server B';\n        }\n    } else {\n        echo 'File upload to Server A failed';\n    }\n} else {\n    echo 'No file uploaded';\n}\n?>