<?php\n$keyword = "I like apples"; // Example keyword input\n\n// For 'With at least one of the words'\nfunction searchWithAtLeastOneWord($pdo, $keyword) {\n    $words = explode(' ', $keyword);\n    $likeClauses = [];\n    $params = [];\n    foreach ($words as $word) {\n        $likeClauses[] = "field LIKE ?";\n        $params[] = "%" . $word . "%";\n    }\n    $query = "SELECT * FROM myTable WHERE " . implode(' OR ', $likeClauses);\n    $stmt = $pdo->prepare($query);\n    $stmt->execute($params);\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// For 'Without the words'\nfunction searchWithoutWords($pdo, $keyword) {\n    $words = explode(' ', $keyword);\n    $notLikeClauses = [];\n    $params = [];\n    foreach ($words as $word) {\n        $notLikeClauses[] = "field NOT LIKE ?";\n        $params[] = "%" . $word . "%";\n    }\n    $query = "SELECT * FROM myTable WHERE " . implode(' AND ', $notLikeClauses);\n    $stmt = $pdo->prepare($query);\n    $stmt->execute($params);\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// Database connection (PDO)\n$dsn = "mysql:host=your_host;dbname=your_dbname";\n$username = "your_username";\n$password = "your_password";\n$options = [\n    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n];\n\ntry {\n    $pdo = new PDO($dsn, $username, $password, $options);\n    \n    // Example usage\n    $resultsWithAtLeastOneWord = searchWithAtLeastOneWord($pdo, $keyword);\n    $resultsWithoutWords = searchWithoutWords($pdo, $keyword);\n    \n    // Output results\n    var_dump($resultsWithAtLeastOneWord);\n    var_dump($resultsWithoutWords);\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>