// Establish the database connection\n$mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the SELECT statement\n$stmt = $mysqli->prepare("SELECT Vote1a FROM qotwVote1a WHERE QuestionId = ? AND MemberId = ?");\n$stmt->bind_param("is", $questionId, $id);\n$stmt->execute();\n$stmt->bind_result($originalVote);\n$stmt->fetch();\n$stmt->close();\n\n// Calculate new vote\n$newVote = $originalVote + $vote;\n\n// Prepare the UPDATE statement\n$update_stmt = $mysqli->prepare("UPDATE qotwVote1a SET Vote1a = ? WHERE QuestionId = ? AND MemberId = ?");\n$update_stmt->bind_param("iis", $newVote, $questionId, $id);\n$update_stmt->execute();\n\n// Check for errors\nif ($update_stmt->error) {\n    die("Error executing query: " . $update_stmt->error);\n}\n\n// Close the statement and connection\n$update_stmt->close();\n$mysqli->close();