<?php\n// Array of known bot user agents\n$botUserAgents = [\n    'googlebot',\n    'bingbot',\n    'slurp',   // Yahoo\n    'duckduckbot',\n    'baiduspider',\n    'yandexbot',\n    'sogou',\n    'exabot',\n    'facebot',\n    'ia_archiver'\n];\n\n// Function to check if the request is from a bot\nfunction isBot() {\n    global $botUserAgents;\n    \n    if (!isset($_SERVER['HTTP_USER_AGENT'])) {\n        return false;\n    }\n    \n    $userAgent = strtolower($_SERVER['HTTP_USER_AGENT']);\n    \n    foreach ($botUserAgents as $bot) {\n        if (strpos($userAgent, $bot) !== false) {\n            return true;\n        }\n    }\n    return false;\n}\n\n// Check if the visitor is a bot\nif (!isBot()) {\n    // Increment the visit count in the database\n    // Assuming you have a database connection $conn and a table `visits`\n    $query = "UPDATE visits SET count = count + 1 WHERE id = 1";\n    mysqli_query($conn, $query);\n} else {\n    // If it's a bot, you may choose to log it or ignore it\n    // For example:\n    error_log("Bot detected: " . $_SERVER['HTTP_USER_AGENT']);\n}\n\n?>