<?php\n   require 'vendor/autoload.php';\n\n   use Phpml\Classification\KNearestNeighbors;\n   use Phpml\ModelManager;\n\n   // Training data\n   $samples = [\n       'I\'m looking to sell my house', \n       'I want to buy a new car', \n       'Get rid of my old bike', \n       'Looking for a new house'\n   ];\n   $labels = ['sell', 'buy', 'sell', 'buy'];\n\n   // Transform the text data to numerical data using TfIdfTransformer or similar\n   // For simplicity, we are skipping this step in the example\n\n   $classifier = new KNearestNeighbors();\n   $classifier->train($samples, $labels);\n\n   // Save the model\n   $modelManager = new ModelManager();\n   $modelManager->saveToFile($classifier, 'model.dat');\n\n   // Predict new post\n   $post = 'I\'m looking for a house';\n   $prediction = $classifier->predict([$post]);\n\n   echo 'Category: ' . $prediction[0]; // Output: Category: buy\n   ?>