function pingWebsite($url) {\n    if (substr($url, 0, 4) != "http") {\n        $url = "http://" . $url;\n    }\n\n    $userAgent = 'Mozilla/5.0 (compatible; MSIE 6.0; Windows NT 5.1; .NET CLR 1.1.4322)';\n\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_HEADER, 1);\n    curl_setopt($ch, CURLOPT_NOBODY, 1);\n    curl_setopt($ch, CURLOPT_USERAGENT, $userAgent);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 15);\n    curl_setopt($ch, CURLOPT_FAILONERROR, 1);\n    curl_setopt($ch, CURLOPT_FRESH_CONNECT, 1);\n\n    $execute = curl_exec($ch);\n\n    $result = [];\n    if (!curl_errno($ch)) {\n        $result['time'] = curl_getinfo($ch, CURLINFO_TOTAL_TIME);\n        $result['size'] = curl_getinfo($ch, CURLINFO_CONTENT_LENGTH_DOWNLOAD);\n        $result['status'] = 'Online';\n    } else {\n        $result['time'] = 0;\n        $result['size'] = 0;\n        $result['status'] = 'Error: ' . curl_error($ch);\n    }\n    curl_close($ch);\n\n    return $result;\n}\n\n// Usage example\n$url = 'https://www.example.com';\n$response = pingWebsite($url);\necho "Status: " . $response['status'] . "\n";\necho "Time: " . $response['time'] . "s\n";\necho "Size: " . $response['size'] . " bytes\n";