<?php\nfunction testOracleConnection($username, $password, $connection_string, $timeout = 5) {\n    $tns = "(DESCRIPTION=(ADDRESS_LIST=(ADDRESS=(PROTOCOL=TCP)(HOST=$connection_string)(PORT=1521)))(CONNECT_DATA=(SID=your_sid)))";\n    $connection = @oci_connect($username, $password, $tns);\n\n    if (!$connection) {\n        $error = oci_error();\n        echo "Could not connect to Oracle database. Error: " . $error['message'];\n        return false;\n    } else {\n        echo "Successfully connected to Oracle database.";\n        oci_close($connection);\n        return true;\n    }\n}\n\n$username = 'your_username';\n$password = 'your_password';\n$connection_string = 'your_hostname_or_ip';\n\nif (!testOracleConnection($username, $password, $connection_string)) {\n    echo "Database is offline or connection parameters are incorrect.";\n}\n?>