$cacheFile = 'cache/tags_count.json';\n$cacheTime = 86400; // 24 hours\n\n// Check if cache file exists and is still valid\nif (file_exists($cacheFile) && (time() - filemtime($cacheFile) < $cacheTime)) {\n    // Read data from cache file\n    $tags = json_decode(file_get_contents($cacheFile), true);\n} else {\n    // Fetch data from database\n    $tags = [];\n    foreach ($database->query("SELECT tag_name, COUNT(tag_id) AS 'tag_count' FROM tags LEFT JOIN videotags ON tags.id = videotags.tag_id GROUP BY tags.id ORDER BY tag_count DESC") as $row) {\n        $tags[] = $row;\n    }\n    // Save data to cache file\n    file_put_contents($cacheFile, json_encode($tags));\n}\n\n// Display tags\nforeach ($tags as $tag) {\n    echo $tag["tag_name"] . ', ';\n}