<?php\n\nclass Config {\n    private $settings;\n    private $file;\n\n    public function __construct($file) {\n        $this->file = $file;\n        if (file_exists($file)) {\n            $this->settings = json_decode(file_get_contents($file), true);\n        } else {\n            $this->settings = [];\n        }\n    }\n\n    public function getConfig(...$keys) {\n        $config = $this->settings;\n        foreach ($keys as $key) {\n            if (isset($config[$key])) {\n                $config = $config[$key];\n            } else {\n                return null;\n            }\n        }\n        return $config;\n    }\n\n    public function setConfig($value, ...$keys) {\n        $config = &$this->settings;\n        foreach ($keys as $key) {\n            if (!isset($config[$key])) {\n                $config[$key] = [];\n            }\n            $config = &$config[$key];\n        }\n        $config = $value;\n        file_put_contents($this->file, json_encode($this->settings, JSON_PRETTY_PRINT));\n    }\n\n    public function removeConfig(...$keys) {\n        $config = &$this->settings;\n        $lastKey = array_pop($keys);\n        foreach ($keys as $key) {\n            if (isset($config[$key])) {\n                $config = &$config[$key];\n            } else {\n                return false;\n            }\n        }\n        if (isset($config[$lastKey])) {\n            unset($config[$lastKey]);\n            file_put_contents($this->file, json_encode($this->settings, JSON_PRETTY_PRINT));\n            return true;\n        }\n        return false;\n    }\n}\n\n// Example Usage\n$config = new Config('settings.json');\necho $config->getConfig('Database', 'AccessSettings', 'Username'); // Output: myDBUsername\n\n$config->setConfig('newDBUsername', 'Database', 'AccessSettings', 'Username'); // Update Username\n$config->setConfig('newDBPassword', 'Database', 'AccessSettings', 'Password'); // Update Password\n\n$config->removeConfig('Database', 'AccessSettings', 'Password'); // Remove Password\n?>