<?php\n// Load the CodeIgniter instance\n$CI =& get_instance();\n\n// Check if the user is logged in\nif ($CI->session->userdata('logged_in')) {\n    // Query the database to check if the status is 'closed'\n    $query = $CI->db->get_where('your_table', array('status' => 'closed'));\n    \n    if ($query->num_rows() > 0) {\n        // The status is 'closed' and the user is logged in\n        // HTML element to be blocked out\n        echo '<div>This element is blocked.</div>';\n    }\n}\n?>