function processFraudForm($crm_number, $datacash_ref, $amount, $sales_date, $domain, $agent_added) {\n       $mysqli = new mysqli("hostname", "username", "password", "database");\n\n       if ($mysqli->connect_error) {\n           die("Connection failed: " . $mysqli->connect_error);\n       }\n\n       $mysqli->set_charset("utf8");\n\n       $stmt = $mysqli->prepare("INSERT INTO fraud_report (crm_number, datacash_ref, amount, sales_date, domain, date_added, agent_added) VALUES (?, ?, ?, ?, ?, NOW(), ?)");\n       $stmt->bind_param("ssssss", $crm_number, $datacash_ref, $amount, $sales_date, $domain, $agent_added);\n\n       if ($stmt->execute()) {\n           $outcome = "Emails sent and database updated.";\n       } else {\n           $outcome = "Something went wrong!";\n       }\n\n       $stmt->close();\n       $mysqli->close();\n\n       return $outcome;\n   }