function holiday_hitlist($tablename, $hit_user){\n\n    global $host, $user, $pass, $dbname;\n\n    $link = mysqli_connect($host, $user, $pass, $dbname);\n\n    print "<div class=\"hit_list\">\n            <h3>My Holiday Hitlist</h3>\n            <p>Five things I want the most, based on my desirability ratings.<br/>You can't go wrong with this stuff!</p>\n            <ol>";\n\n    $sql = "SELECT title, URL, price FROM $tablename WHERE user=? AND rank >= 3 ORDER BY date DESC LIMIT 5";\n    $stmt = mysqli_prepare($link, $sql);\n    mysqli_stmt_bind_param($stmt, 's', $hit_user);\n    mysqli_stmt_execute($stmt);\n    $result = mysqli_stmt_get_result($stmt);\n\n    while($row = mysqli_fetch_array($result)) {\n        $title = $row['title'];\n        $url = $row['URL'];\n        $price = "$" . $row['price'];\n        print "<li><a href=\"$url\" target=\"_blank\">$title</a> $price</li>";\n    }\n    print "</ol></div>";\n\n    mysqli_stmt_close($stmt);\n    mysqli_close($link);\n}