<?php\n\n/**\n * Implements hook_aggregator_fetch().\n */\nfunction custom_aggregator_aggregator_fetch($feed) {\n  // Custom fetching logic, if needed.\n}\n\n/**\n * Implements hook_aggregator_parse().\n */\nfunction custom_aggregator_aggregator_parse($feed) {\n  // Allow additional HTML attributes in the feed items.\n  $allowed_tags = '<p><a><img><br><b><strong><i><em>';\n  $allowed_attributes = array(\n    'style',\n    'dir',\n    'title',\n    'class',\n    'href'\n  );\n\n  foreach ($feed->items as &$item) {\n    $item->description = filter_xss($item->description, explode(' ', $allowed_tags), $allowed_attributes);\n  }\n}