$db = Zend_Db_Table::getDefaultAdapter();\n$categoryId = 1; // Provide the parent category ID here\n\n$sql = "WITH RECURSIVE CategoryHierarchy AS (\n            SELECT category_id, category_name, parent_id\n            FROM categories\n            WHERE category_id = :categoryId\n\n            UNION ALL\n\n            SELECT c.category_id, c.category_name, c.parent_id\n            FROM categories c\n            INNER JOIN CategoryHierarchy ch ON ch.category_id = c.parent_id\n        )\n        SELECT i.item_id, i.item_name\n        FROM items i\n        INNER JOIN item_category ic ON i.item_id = ic.item_id\n        INNER JOIN CategoryHierarchy ch ON ic.category_id = ch.category_id";\n\n$items = $db->fetchAll($sql, ['categoryId' => $categoryId]);\n\nforeach ($items as $item) {\n    echo $item['item_name'] . "\n";\n}