class MyDB {\n    private $dbConn;\n    private $dbSql;\n    private $dbBind = array();\n    private $dbQuery;\n\n    private function escape($value) {\n        if (get_magic_quotes_gpc()) {\n            $value = stripslashes($value);\n        }\n        return mysqli_real_escape_string($this->dbConn, $value);\n    }\n\n    public function connect($dbHost, $dbName, $dbUser, $dbPass) {\n        $this->dbConn = mysqli_connect($dbHost, $dbUser, $dbPass, $dbName);\n        if (mysqli_connect_error()) {\n            die("Connection failed: " . mysqli_connect_error());\n        }\n    }\n\n    public function prep($sql) {\n        $this->dbSql = $sql;\n    }\n\n    public function bind($hook, $value) {\n        $this->dbBind[$hook] = $this->escape($value);\n    }\n\n    public function run() {\n        $sql = $this->dbSql;\n        if (is_array($this->dbBind)) {\n            foreach ($this->dbBind as $hook => $value) {\n                $sql = str_replace($hook, "'" . $value . "'", $sql);\n            }\n        }\n        $this->dbQuery = mysqli_query($this->dbConn, $sql);\n        if (!$this->dbQuery) {\n            die("Error: " . mysqli_error($this->dbConn));\n        }\n        $this->dbBind = array();\n        return $this->numRows();\n    }\n\n    public function numRows() {\n        return mysqli_affected_rows($this->dbConn);\n    }\n}\n\n// Usage example\n$MyDB = new MyDB();\n$MyDB->connect('localhost', 'your_db_name', 'your_db_user', 'your_db_password');\n$date = date('Y-m-d H:i:s');\n$MyDB->prep("INSERT INTO `demo` (`id`, `name`, `score`, `dept`, `date`) VALUES (:id, :name, :score, :dept, :date)");\n$MyDB->bind(':id', 1);\n$MyDB->bind(':name', 'James Kablammo');\n$MyDB->bind(':score', 1205550);\n$MyDB->bind(':dept', 'Marketing');\n$MyDB->bind(':date', $date);\n$MyDB->run();