<?php\nfunction getCoordinates($zip) {\n    $servername = "your_servername";\n    $username = "your_username";\n    $password = "your_password";\n    $dbname = "your_database";\n\n    // Create connection\n    $conn = new mysqli($servername, $username, $password, $dbname);\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    $stmt = $conn->prepare("SELECT LATITUDE, LONGITUDE FROM zipcodes WHERE ZIP = ?");\n    $stmt->bind_param("s", $zip);\n    $stmt->execute();\n    $stmt->bind_result($latitude, $longitude);\n    $stmt->fetch();\n    $stmt->close();\n    $conn->close();\n\n    return array('latitude' => $latitude, 'longitude' => $longitude);\n}\n\nfunction haversineGreatCircleDistance($latitudeFrom, $longitudeFrom, $latitudeTo, $longitudeTo, $earthRadius = 6371) {\n    // convert from degrees to radians\n    $latFrom = deg2rad($latitudeFrom);\n    $lonFrom = deg2rad($longitudeFrom);\n    $latTo = deg2rad($latitudeTo);\n    $lonTo = deg2rad($longitudeTo);\n\n    $latDelta = $latTo - $latFrom;\n    $lonDelta = $lonTo - $lonFrom;\n\n    $angle = 2 * asin(sqrt(pow(sin($latDelta / 2), 2) +\n      cos($latFrom) * cos($latTo) * pow(sin($lonDelta / 2), 2)));\n    return $angle * $earthRadius;\n}\n\n$zip1 = '10001'; // First ZIP code\n$zip2 = '90210'; // Second ZIP code\n\n$coords1 = getCoordinates($zip1);\n$coords2 = getCoordinates($zip2);\n\n$distance = haversineGreatCircleDistance(\n    $coords1['latitude'], $coords1['longitude'],\n    $coords2['latitude'], $coords2['longitude']\n);\n\necho "The distance between ZIP code $zip1 and ZIP code $zip2 is " . $distance . " km.";\n?>